mapper.select:
SELECT
h.own_hotel_id,
h.count_room AS total_room,
(
SELECT COUNT(*)
FROM reservation r
WHERE r.own_hotel_id = h.own_hotel_id
AND r.start_date <= #{startDate}
AND r.end_date >= #{endDate}
) AS reserved_room
FROM own_hotel h
WHERE h.own_hotel_id IN


    #{id}


HAVING total_room > reserved_room;
